const apiInfo = await getEndpointInfo( endpointPath );
switch(apiInfo.commandName){
  //======================================================================
  case "START_UP":{
    await startUp( null, false );
    return {
      "userMessage": "再接続しました。"
    };
  }
  //======================================================================
  case "CLEAR_CACHE":{
    await clearCache();
    return {
      "userMessage": "キャッシュデータを削除しました。"
    };
  }
  //======================================================================
  case "LIST_TABLES":{
    const { tables, total } = await listTables(
      queryParameters["page_tables"],
      35,
      false, //isTrash
    );
    return {
      "tables": tables,
      "tables_total": total,
    };
  }
  //======================================================================
  case "LIST_COLUMNS":{
    const { columns, total } = await listColumnsForGUI(
      queryParameters["table"],
      queryParameters["page_columns"],
      35,
      false, //isTrash
    );
    return {
      "columns": columns,
      "columns_total": total,
    };
  }
  //======================================================================
  case "CREATE_TABLE":{
    const { tableId, message } = await createTable( requestBody["tableName"] );
    return {
      "tableId": tableId,
      "userMessage": message,
      "nextUrl": "../",
    };
  }
  //======================================================================
  case "CREATE_PRIMITIVE_COLUMN":{
    const { columnId, message } = await createColumn(
      queryParameters["table"],
      requestBody["columnName"],
      requestBody["dataType"],
      null
    );
    return {
      "columnId": columnId,
      "userMessage": message,
      "nextUrl": `../?table=${queryParameters["table"]}`,
    };
  }
  //======================================================================
  case "CREATE_POINTER_COLUMN":{
    const { columnId, message } = await createColumn(
      queryParameters["table"],
      requestBody["columnName"],
      "POINTER",
      requestBody["parentTableId"]
    );
    return {
      "columnId": columnId,
      "userMessage": message,
      "nextUrl": `../?table=${queryParameters["table"]}`,
    };
  }
  //======================================================================
  case "UPDATE_TABLE_NAME":{
    const message = await updateTableName(
      requestBody["tables"],
    );
    return {
      "userMessage": message,
      "nextUrl": "../",
    };
  }
  //======================================================================
  case "UPDATE_COLUMN_NAME":{
    const message = await updateColumnName(
      requestBody["columns"],
    );
    return {
      "userMessage": message,
      "nextUrl": `../?table=${queryParameters["table"]}`,
    };
  }
  //======================================================================
  case "REGENERATE_PAGE":{
    await regeneratePage( Number(queryParameters["page_id"]) );
    return {
      "nextUrl": "./?" + await convertQuery_core(queryParameters),
    };
  }
  //======================================================================
  case "CREATE_PAGE":{
    await createPage( Number(queryParameters["parent_id"]) );
    return {
      "nextUrl": "./?" + await convertQuery_core(queryParameters),
    };
  }
  //======================================================================
  case "DELETE_PAGE":{
    await deletePage( Number(queryParameters["page_id"]) );
    return {
      "nextUrl": "./?" + await convertQuery_core(queryParameters),
    };
  }
  //======================================================================
  case "COPY_PAGE":{
    const { copyingPageId, cuttingPageId } = await copyPage( Number(queryParameters["page_id"]) );
    return {
      "copyingPageId": copyingPageId,
      "cuttingPageId": cuttingPageId,
    };
  }
  //======================================================================
  case "CUT_PAGE":{
    const { copyingPageId, cuttingPageId } = await cutPage( Number(queryParameters["page_id"]) );
    return {
      "copyingPageId": copyingPageId,
      "cuttingPageId": cuttingPageId,
    };
  }
  //======================================================================
  default:
    throw `サポートされていないAPIコマンドです。\ncommandName = ${apiInfo.commandName}`;
}
