const dataType = await getDataType( columnId );
switch (dataType) {
    case "INTEGER":
        if (!isNaN(inputText)) {
            // 数値に変換できる場合
            inputText = Number(inputText);
            if (!Number.isInteger(inputText)) {
                throw "小数は指定できません。指定できるのは整数のみです。";
            }
            return inputText;
        }
        // 数値に変換できない場合
        if (inputText) {
            // 空欄ではない場合
            throw "指定できるのは整数のみです。";
        }
        // 空欄の場合
        if (isRequired === false) {
            // 空欄OKの場合
            return null;
        }
        // 空欄NGの場合
        throw "必須項目が空欄です。";
    case "REAL":
        if (!isNaN(inputText)) {
            // 数値に変換できる場合
            return Number(inputText);
        }
        // 数値に変換できない場合
        if (inputText) {
            // 空欄ではない場合
            throw "指定できるのは整数のみです。";
        }
        // 空欄の場合
        if (isRequired === false) {
            // 空欄OKの場合
            return null;
        }
        // 空欄NGの場合
        throw "必須項目が空欄です。";
    case "TEXT":
        if (inputText) {
            // 空欄ではない場合
            // 文字列に変換して、前後の空白を切り取って、よく確かめる。
            if (String(inputText).trim()) {
                // やっぱり空欄ではない場合
                return String(inputText);
            }
        }
        // 空欄の場合
        if (isRequired === false) {
            // 空欄OKの場合
            return null;
        }
        throw "必須項目が空欄です。";
    case "BOOL":
        if (inputText === true) {
            return true;
        }
        else if (inputText === false) {
            return false;
        }
        if (String(inputText).toLowerCase() === "true") {
            return true;
        }
        else if (String(inputText).toLowerCase() === "false") {
            return false;
        }
        if (!isNaN(inputText)) {
            // 数値に変換できる場合
            return Number(inputText) !== 0;
        }
        // 数値に変換できない場合
        if (inputText) {
            // 空欄ではない場合
            throw "指定できるのはBOOL値のみです。";
        }
        // 空欄の場合
        if (isRequired === false) {
            // 空欄OKの場合
            return null;
        }
        // 空欄NGの場合
        throw "必須項目が空欄です。";
    default:
        throw `サポートされていないデータ型です。detaType="${dataType}"`;
}
