await startUp("http://localhost:3000/", true);
//
const { tableId: tableId1 } = await createTable("学年");
const { columnId: columnId1 } = await createColumn( tableId1, "学年", "INTEGER", null );
const { recordId: recordId } = await createRecord( tableId1, {
  [columnId1]: 3,
});
//
const { tableId: tableId2 } = await createTable("名簿");
const { columnId: columnId2  } = await createColumn( tableId2, "学年", "POINTER", tableId1 );
const { columnId: columnId3  } = await createColumn( tableId2, "氏名", "TEXT", null );
const { recordId: recordId2 } = await createRecord( tableId2, {
  [columnId2]: recordId,
  [columnId3]: "田中太郎",
});
const { recordId: recordId3 } = await createRecord( tableId2, {
  [columnId2]: recordId,
  [columnId3]: "鈴木信也",
});
//
const { tableId: tableId3 } = await createTable("成績表");
const { columnId: columnId4  } = await createColumn( tableId3, "学生", "POINTER", tableId2 );
const { columnId: columnId5  } = await createColumn( tableId3, "科目", "TEXT", null );
const { columnId: columnId6  } = await createColumn( tableId3, "得点", "INTEGER", null );
const { recordId: recordId4 } = await createRecord( tableId3, {
  [columnId4]: recordId2,
  [columnId5]: "国語",
  [columnId6]: 34,
});
//
await close();

const primaryKey = await getPrimaryKey( tableId );
const columns = await listColumnsAll( tableId );
if ( !pageNumber ) {
    pageNumber = 1;
}
if (!(pageNumber >= 1)) {
    pageNumber = 1;
}
const [{ "COUNT(*)": recordsTotal }] = await runSqlReadOnly(
    `SELECT COUNT(*) FROM ${tableId};`,
    {},
);
let offset = onePageMaxSize * (pageNumber - 1);
if( !isNaN(focusRecordId) && focusRecordId>=1 ){
    const [{ "COUNT(*)": offset2 }] = await runSqlReadOnly(
        `SELECT COUNT(*)
            FROM ${tableId}
            WHERE sort_number > (
                SELECT sort_number
                    FROM ${tableId}
                    WHERE ${primaryKey} = :recordId
                    LIMIT 1
            )
            ORDER BY sort_number DESC;`,
        {
            ":recordId": focusRecordId,
        },
    );
    offset = offset2 - (offset2 % onePageMaxSize);
}
else if( !isNaN(pasteRecordId) && pasteRecordId>=1 ){
    const [{ "COUNT(*)": offset2 }] = await runSqlReadOnly(
        `SELECT COUNT(*)
            FROM ${tableId}
            WHERE sort_number > (
                SELECT sort_number
                    FROM ${tableId}
                    WHERE ${primaryKey} = :recordId
                    LIMIT 1
            )
            ORDER BY sort_number DESC;`,
        {
            ":recordId": pasteRecordId,
        },
    );
    offset = offset2 - (offset2 % onePageMaxSize);
}
if( offset >= recordsTotal ){
    offset = recordsTotal;
}
// 「sqlite_master」と結合させることで、実際に存在するテーブルのみに絞り込む
const oldRecords = await runSqlReadOnly(
    `SELECT *
        FROM ${tableId}
        ORDER BY sort_number DESC
        LIMIT :limit OFFSET :offset;`,
    {
        ":limit": onePageMaxSize,
        ":offset": offset,
    },
);
// 切り取り中のレコードを取得する
const cuttingRecordId = await getCuttingRecord( tableId );
// コピー中のレコードを取得する
const copyingRecordId = await getCopyingRecord( tableId );
const newRecords = [];
for( const oldRecord of oldRecords ){
    const recordId = oldRecord[primaryKey];
    const newRecord = {
        "id": String(recordId),
        "isFocus": (recordId===focusRecordId) ? true : false,
        "isCopying": (recordId===copyingRecordId) ? true : false,
        "isCutting": (recordId===cuttingRecordId) ? true : false,
        "isPaste": (recordId===pasteRecordId) ? true : false,
    };
    for( let i=0; i<columns.length; i++ ){
        const columnId = columns[i]["id"];
        let value = oldRecord[columnId];
        newRecord[ "field" + i ] = value;
    }
    newRecords.push(newRecord);
}
return {
    "columns": columns,
    "records": newRecords,
    "recordsTotal": recordsTotal,
    "pageNumber": Math.floor(offset/onePageMaxSize)+1,
};

const newFields = {};
for( let i=0; i<columns.length; i++ ){
    const columnId = columns[i]["id"];
    const inputText = columns[i]["newField"];
    newFields[columnId] = await formatField( inputText, columnId, false );
}
return await createRecord( tableId, newFields );
